create table IF NOT EXISTS gifts (gift_id bigint generated by default as identity, comment varchar(255), priority varchar(255) not null check (priority in ('HIGH','MIDDLE','LOW')), status varchar(255) not null check (status in ('UNBOOKED','BOOKED')), owner_user_id bigint, product_product_id bigint, primary key (gift_id))
create table IF NOT EXISTS products (product_id bigint generated by default as identity, description varchar(255), image varchar(255), name varchar(255) not null, price numeric(38,2), score float(24), primary key (product_id))
create table IF NOT EXISTS users (user_id bigint generated by default as identity, birthday date not null, first_name varchar(255) not null, last_name varchar(255) not null, password varchar(255) not null, roles varchar(255) array not null, surname varchar(255), username varchar(255) not null, primary key (user_id))
alter table if exists users drop constraint if exists UK_r43af9ap4edm43mmtq01oddj6
alter table if exists users add constraint UK_r43af9ap4edm43mmtq01oddj6 unique (username)
alter table if exists gifts add constraint FKcjy6ww1d27s8a0ptpmoy9s4w0 foreign key (owner_user_id) references users
alter table if exists gifts add constraint FKtij8ymv973egpcudufvf5xkj6 foreign key (product_product_id) references products